mapper.insert:
INSERT INTO react_board(num, writer, email, subject, reg_date, 
				readcount, ref, re_step, re_level, content, ip, upload)
VALUES(react_board_num_seq.nextval, #{writer}, #{email}, #{subject}, sysdate,	0,


			react_board_num_seq.nextval


			#{ref}


	, #{re_step}, #{re_level}, #{content, jdbcType=VARCHAR}, #{ip, jdbcType=VARCHAR}, #{upload, jdbcType=VARCHAR}

 )

mapper.update:
UPDATE react_board
SET subject=#{subject}, email=#{email, jdbcType=VARCHAR}, content=#{content, jdbcType=VARCHAR}

	, upload=#{upload, jdbcType=VARCHAR}

WHERE num=#{num}
